"use strict";
jQuery(function(e){
    if("undefined"==typeof woocommerce_price_slider_params) return!1;
    e("input#max_price").hide(),e(".price_slider, .price_label").show();
    var c=e(".price_slider_amount #max_price").data("max"),
        _=parseInt(c,10);
    woocommerce_price_slider_params.max_price && (_=parseInt(woocommerce_price_slider_params.max_price,10));
    e(document.body).bind("price_slider_create price_slider_slide",function(r,c,i){
        e(".price_slider_amount span.to").html(accounting.formatMoney(i,{
            symbol:woocommerce_price_slider_params.currency_format_symbol,
            decimal:woocommerce_price_slider_params.currency_format_decimal_sep,
            thousand:woocommerce_price_slider_params.currency_format_thousand_sep,
            precision:woocommerce_price_slider_params.currency_format_num_decimals,
            format:woocommerce_price_slider_params.currency_format
        }));
        e(document.body).trigger("price_slider_updated",[null,i]);
    });
    e(".price_slider").slider({
        range:"max",
        animate:!0,
        min:0,
        max:c,
        value:_,
        create:function(){
            e(".price_slider_amount #max_price").val(_),
            e(document.body).trigger("price_slider_create",[null,_]);
        },
        slide:function(r,c){
            e("input#max_price").val(c.value),
            e(document.body).trigger("price_slider_slide",[null,c.value]);
        },
        change:function(r,c){
            e(document.body).trigger("price_slider_change",[null,c.value]);
        }
    });
});